    private void btnListarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnListarActionPerformed
        GrupoControl g = new GrupoControl();

        try {
            List<Grupo> grupos = g.verGrupo();
            String[][] grupoArray = new String[grupos.size()][2];

            if (grupos.size() > 0) {
                for (int i = 0; i < grupos.size(); i++) {
                    grupoArray[i][0] = grupos.get(i).getCveGru();
                    grupoArray[i][1] = grupos.get(i).getNomGru();
                }

                jTable1.setModel(new javax.swing.table.DefaultTableModel(
                        grupoArray,
                        new String[]{"Clave Grupo", "Nombre Grupo"}));
            } else {
                JOptionPane.showMessageDialog(this, "No existe ningún grupo agregado");
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Error" + e.getMessage());
        }
        g.cerrarDB();
    }//GEN-LAST:event_btnListarActionPerformed

    private void btnModificarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnModificarActionPerformed
        if (!txt_cveGruMod.getText().isEmpty() && !txt_nomGruMod.getText().isEmpty()) {
            GrupoControl g = new GrupoControl();
            Boolean grupo = g.buscarGrupo(txt_cveGruMod.getText());
            System.out.println("resultado de la busqueda de grupos" + grupo);
            if (grupo == TRUE ) {
            Grupo a = new Grupo(txt_cveGruMod.getText(), txt_nomGruMod.getText());
            g.eliminarGrupo(txt_cveGruMod.getText());
            GrupoControl b = new GrupoControl();
            b.agregarGrupo(a);
            JOptionPane.showMessageDialog(this, "Modificado"); 

            }else{
              JOptionPane.showMessageDialog(this, "No existe un resgistri con esa clave");      
            }
            
        }
        if (txt_cveGruMod.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Ingrese un clave para el grupo");
            if (txt_nomGruMod.getText().isEmpty()) {
                JOptionPane.showMessageDialog(this, "Ingrese un nombre para el Grupo");
            }
        }

        
    }//GEN-LAST:event_btnModificarActionPerformed

    private void btnEliminar1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEliminar1ActionPerformed
        GrupoControl GrupoControl = new GrupoControl();
        if (JOptionPane.showConfirmDialog(this, "Realmente deseas eliminar el grupo?") == 0) {
            GrupoControl.eliminarGrupo(txt_cveGruEli.getText());
            JOptionPane.showMessageDialog(this, "Eliminado");
        }
    }//GEN-LAST:event_btnEliminar1ActionPerformed

