    private void btnAgregarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAgregarActionPerformed
        if (!txt_cveGru.getText().isEmpty() && !txt_nomGru.getText().isEmpty()) {
            GrupoControl g = new GrupoControl();
            Boolean grupo = g.buscarGrupo(txt_cveGru.getText());
            System.out.println("resultado de la busqueda de grupos" + grupo);
            if (!grupo == TRUE ) {
            Grupo a = new Grupo(txt_cveGru.getText(), txt_nomGru.getText());
            g.agregarGrupo(a);
            JOptionPane.showMessageDialog(this, "Agregado"); 
            
            }else{
              JOptionPane.showMessageDialog(this, "Ya existe un registro con esta clave");      
            }
            
        }
        if (txt_cveGru.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Ingrese un clave para el grupo");
            if (txt_nomGru.getText().isEmpty()) {
                JOptionPane.showMessageDialog(this, "Ingrese un nombre para el Grupo");
            }
        }

    }//GEN-LAST:event_btnAgregarActionPerformed

